mapper.select:
SELECT
bct.*,
bat.title as articleTitle
FROM blog_comment_table bct JOIN blog_article_table bat ON bct.article_id = bat.id


        bct.article_id = #{articleId}


        AND bct.content LIKE "%"#{content}"%"


        AND bct.uid = #{uid}


order by create_time desc

    LIMIT #{limit}


    OFFSET #{offset}

